let highlightOpen=!1;function OpenHighlight(e,t=!0){if(highlightOpen)e.remove();else{document.getElementById("highlight-frame").prepend(e);const n=document.getElementById("highlight");n.classList.add("visible"),t&&n.classList.add("fit"),highlightOpen=!0}}function OpenHighlightImage(e){const t=document.createElement("img");t.id="highlight-item",t.alt=e.alt,t.src=e.src,OpenHighlight(t)}function OpenHighlightVideo(e){const t=document.createElement("video");t.id="highlight-item",t.setAttribute("controls","controls"),t.innerHTML=e.innerHTML,t.load(),OpenHighlight(t)}function OpenHighlightSample(e){let n=e;for(;!n.classList.contains("sample");)n=n.parentNode;const t=document.createElement("div");t.id="highlight-item",t.classList.add("sample","expanded"),t.innerHTML=n.innerHTML,t.querySelector(".expand").remove(),t.querySelector(".info").removeAttribute("onclick"),OpenHighlight(t,!1)}function CloseHighlight(e){highlightOpen&&(e.target.id==="highlight"||document.getElementById("highlight-close").contains(e.target))&&(document.getElementById("highlight-item").remove(),document.getElementById("highlight").classList.remove("visible","fit"),highlightOpen=!1)}